CREATE TABLE encounter ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR(255), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR(255), facility_id BIGINT, encounter_date date NOT NULL, person_id BIGINT NOT NULL, uuid VARCHAR(255) NOT NULL, visit_id BIGINT NOT NULL, service_code VARCHAR(255) NOT NULL, status VARCHAR(255) NOT NULL, archived INTEGER, CONSTRAINT pk_encounter PRIMARY KEY (id) );
